    // ... (same as previous App.js)
  };

  return (
    <div className="mt-4">
      <h2>Create Report</h2>
      <form>
        <div className="form-group">
          <label htmlFor="id">ID</label>
          <input type="number" className="form-control" id="id" ref={reportIdInput} />
        </div>
        {/* ... other input fields */}
        <button
          type="button"
          className="btn btn-primary"
          onClick={() =>
            createReport(
              reportIdInput.current.value,
              nameInput.current.value,
              socialInput.current.value,
              mathInput.current.value,
              naturalScienceInput.current.value
            )
          }
        >
          Create Report
        </button>
      </form>
    </div>
  );
}

export default CreateReport;
